mapper.update:
UPDATE  t_article


    comment_count=comment_count+#{num}


    collect_count=collect_count+#{num}


    praise_count=praise_count+#{num}


    click_count=click_count+#{num}


    report_count=report_count+#{num}


where id=#{id}

mapper.select:
SELECT  b.*

  ,t.status as followStatus
  ,p.status as praiseStatus

from (SELECT
a.*,
u.nick_name as nickName,
u.head_img headImg,
(select count(1) from t_user_report where article_id = a.id and status!=0) userReportStatus
from t_article a LEFT JOIN  b_user u on a.user_id = u.id
LEFT JOIN  t_user_report c on a.id =c.article_id
where a.status!=0

  and a.id=#{id}


  and a.status=#{status}


  and a.is_show=#{isShow}


  and a.type=#{type}


  and a.title like CONCAT('%',#{title},'%')


  and a.content like CONCAT('%',#{content},'%')


  and a.report_count >= #{reportCount}


  and a.labels like CONCAT('%',#{labels},'%')


  and u.username=#{username}


  and a.user_id=#{authorId}


  and a.create_time >=#{startTime}


  and a.create_time <=#{endTime}


  having userReportStatus=0


  having userReportStatus>1

) b

  left join t_user_follow t on b.user_id=t.follow_id and t.user_id=#{userId}
  left join t_article_collect_praise p on p.article_id = b.id and p.type=0 and p.user_id=#{userId}

ORDER  by b.create_time desc

SELECT  b.*,b.info as reportInfo

  ,t.status as followStatus
  ,p.status as praiseStatus

from (SELECT
a.*,
m.info,
u.nick_name as nickName,
u.name as username,
u.head_img
from t_user_report m left join t_article a on m.article_id=a.id LEFT JOIN  b_user u on a.user_id = u.id
where a.status!=0 and m.status!=0

  and a.id=#{id}


  and a.status=#{status}


  and a.is_show=#{isShow}


  and a.type=#{type}


  and a.title like CONCAT('%',#{title},'%')


  and a.content like CONCAT('%',#{content},'%')


  and a.report_count >= #{reportCount}


  and a.labels like CONCAT('%',#{labels},'%')


  and u.username=#{username}


  and a.create_time >=#{startTime}


  and a.create_time <=#{endTime}
) b

  left join t_user_follow t on b.user_id=t.follow_id and t.user_id=#{userId}
  left join t_article_collect_praise p on p.article_id = b.id and p.type=0 and p.user_id=#{userId}

ORDER  by b.create_time desc

SELECT
a.*
from t_article a
where a.status!=0

  and a.id=#{id}


  and a.status=#{status}


  and a.type=#{type}


  and a.is_show=#{isShow}


  and a.title like CONCAT('%',#{title},'%')


  and a.content like CONCAT('%',#{content},'%')


  and a.report_count >= #{reportCount}


  and a.labels like CONCAT('%',#{labels},'%')


  and a.create_time >=#{startTime}


  and a.create_time <=#{endTime}

ORDER  by a.sort desc,a.create_time  desc

SELECT
a.*
from t_article a
where a.status!=0

  and a.id=#{id}


  and a.status=#{status}


  and a.type=#{type}


  and a.is_show=#{isShow}


  and a.report_count >= #{reportCount}


  and a.labels like CONCAT('%',#{labels},'%')


  and a.create_time >=#{startTime}


  and a.create_time <=#{endTime}

ORDER  by a.sort desc,a.create_time desc limit 4

select.if:
select a.* from (select t.*,u.nick_name,u.head_img,p.status as praiseStatus from t_article t LEFT JOIN b_user u on t.user_id = u.id left join t_article_collect_praise p on p.article_id = t.id and p.type=0 and p.user_id=#{userId} where 1=1 and t.user_id in
#{item}
order by t.create_time desc limit 0,1000) a union select a.* from (select t.*,u.nick_name,u.head_img,p.status as praiseStatus from t_article t LEFT JOIN b_user u on t.user_id = u.id left join t_article_collect_praise p on p.article_id = t.id and p.type=0 and p.user_id=#{userId} where 1=1 and t.user_id not in
#{item}
order by t.create_time desc limit 0,1000) a
