mapper.select:
SELECT g.*, (CASE g.is_verfiy WHEN 1 then '审核中' WHEN 2 then '审核通过' WHEN 3 then '审核未通过' ELSE '状态未知' END ) good_estate, g.cost AS good_soldPrice , cate.name AS cate_name, c.name as orgName FROM p_goods g LEFT JOIN p_pawn_cate cate ON g.cate_code = cate.code left join p_pawn_org c on g.org_id = c.id
g.source = #{source}
and g.org_id = #{orgId}
and g.TYPE =#{type}
and g.name like '%${name}%'
ORDER BY g.create_time desc

mapper.update:
update p_goods
set is_verfiy = #{isVerfiy,jdbcType=TINYINT} + 1,refuse_info=#{refuseInfo}
where id = #{id}

update p_goods
set create_time = now(),
state = #{state}
where id = #{id}

mapper.select:
select
a.*
,b.integral orgIntegral
,b.name orgName

    ,(select  count(1) from p_goods_auction_online_log where status = 1 and now()> auction_start_time and goods_id = a.id) onlineCnt

from p_goods a left join p_pawn_org b on a.org_id = b.id
where 1=1 and is_online>-1

  and  a.cate_code = #{cateCode,jdbcType=INTEGER}


    and  a.is_online = #{isOnline,jdbcType=TINYINT}


    and a.state = #{state}


    and a.org_id = #{orgId}


    and a.type = #{type}


    and a.name like CONCAT('%',#{name},'%')


    and a.source = #{source}


    and a.deal_type = #{dealType}


    and a.goods_owner = #{goodsOwner}


    and   a.reason_of_dismounting = #{reasonOfDismounting,jdbcType=VARCHAR}


    and  a.is_verfiy = #{isVerfiy,jdbcType=TINYINT}


    and a.source in

        #{item}



    and a.total >=#{startTotal}

order by a.sort_order desc,a.is_suggest desc,a.create_time desc

    limit #{limit}

mapper.update:
update p_goods set is_online = #{isOnline},max_aution_id=#{maxAutionId},max_auction=#{maxAuction}

    ,online_time = #{onlineTime}

where id = #{id}

mapper.select:
select  ifnull(count(1),0) from p_goods where is_online=1 and deal_type=2 and now()>= auction_start_time and now() <=auction_end_time

    and org_id = #{orgId}


    and goods_owner = #{goodsOwner}
