mapper.select:
SELECT o.*, (CASE o.state WHEN -1 THEN '已取消' WHEN 1 THEN '等待付款' WHEN 2 THEN '已付款' WHEN 3 THEN '已发货' WHEN 4 THEN '确认收货' WHEN 5 THEN '已评价' ELSE '状态未知' END ) order_estate, (select c.trade_no from b_pay_log c where o.id=c.order_id order by c.create_time desc limit 1) AS tradNo FROM p_order o
and o.org_id = #{orgId}
and o.goods_source = #{goodsSource}
and o.goods_name like '%${goodsName}%'
ORDER BY o.create_time desc

select A.*, B.express_state as expressState, B.id as expressId, D.id as userGoodsId, D.is_sell as isSell from p_order A left join p_express B on A.id = B.fid left join p_goods C on A.goods_id = C.id left join p_user_goods D on D.id = C.goods_id where A.user_id = #{user_id}
and A.is_del = #{is_del}
and A.ref_state != 0
and A.state = #{orderState} and A.ref_state = 0
and A.comment_state=#{commentState}
order by A.create_time desc

SELECT t.*, u.account goodsOwnerAccount, u.nick_name goodsOwnerNick, u.NAME goodsOwnerName FROM p_order t LEFT JOIN p_goods g ON t.goods_id = g.id LEFT JOIN b_user u ON g.goods_owner = u.id WHERE t.goods_source IN ( 6, 7 )
and t.goods_name like '%${goodsName}%'
and t.state=#{state}
and t.ref_state=#{ref_state}
and t.code like '%${orderCode}%'
and t.is_del = 0 ORDER BY t.create_time desc
